export interface NodeInterface {
    on(type: 'input', callback: (msg: any, done?: any) => void): any;
    on(type: 'close', callback: (done?: any) => void): any;
    send(msg: any): any;
    status(status: any): any;
    log(msg: string): any;
    warn(msg: string): any;
    error(msg: string): any;
}
export interface ConfigNode {
    token: string;
    group: string;
}
